mapper.insert:
insert into t_fss_usergroup_device (device_id, power_ids, user_group_id,device_kind
  )
values (#{ApeID,jdbcType=VARCHAR}, #{PowerIDs,jdbcType=VARCHAR}, #{UserGroupID,jdbcType=INTEGER},#{ApeKind,jdbcType=INTEGER}
  )

mapper.select:
select ugd.user_group_id,ugd.device_id,power_ids,device.device_kind,device.device_name device_name,device.precinct_id precinct_id from t_fss_usergroup ug left join t_fss_usergroup_device ugd on ug.user_group_id=ugd.user_group_id left join t_cfg_device device on ugd.device_id=device.device_id RIGHT JOIN t_scim_facetask f on ugd.device_id=f.camera_id where 1=1
and ug.user_group_id = #{UserGroupID,jdbcType=INTEGER}
and device.device_kind=4 or device.device_kind = 10 order by f.create_time asc
